import React, {useEffect, useState, useRef} from 'react';
import styled from "styled-components";
import { useForm } from "react-hook-form";
import { Link } from "react-router-dom";

//Styled
const Container = styled.div`
    position: relative;
    top: 150px;
    width: 80%;
    padding-bottom: 40px;
`;
const SearchWrapper = styled.div`
    position: relative;
    min-height: 100px;
    left: 10%;
    padding: 20px;
    background-color: transparent;
    border: 2px solid #000;
`;
const SelectorWrapper = styled.div`
    display: flex;
    align-items: center;
    font-size: 18px;
    font-weight: 600;

    > select {
        font-size: 18px;
        margin-left: 10px;
    }
`;
const SubmitWrapper = styled.div`
    position: absolute;
    bottom: 20px;
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
`;
const SubmitBtn = styled.button`
    background-color: transparent;
    border: 2px solid #000;
    font-size: 24px;  
`;
const TableWrapper = styled.div`
    position: relative;
    top: 20px;
    left: 10%;
    border: 2px solid #333;
    > table {
        width: 100%;
        border-collapse: collapse;
    }
    colgroup {
        border-right: 1px solid #000;
        
    }
    thead > tr {
        background-color: #EEEEEE;
    }
    tbody > tr {
        &:nth-child(2n) {
            background-color: #EEEEEE;
        }
    }
    td, th {
        position: relative;
        border-left: 2px solid #000;
        &:first-child {
            border-left: none;
        }
        text-align: center;
        padding: 5px;
    }
    td {
        span {
            font-size: 24px;
            cursor: pointer;
            color: #000;
        }
    }
    th {
        span {
            position: absolute;
            top: 3px;
            right: 0;
            color: #000;
            cursor: pointer;
        }
    }
`;
const PaginationWrapper = styled.div`
    position: relative;
    top: 30px;
    left: 10%;
    display: flex;
    justify-content: flex-end;  
`;
const PaginationBtn = styled.button`
    width: 30px;
    height: 30px;
    display: flex;
    align-items: center;
    justify-content: center; 
    background-color: ${props => props.active ? '#8FB9F7' : 'trasparent'};
    border: 2px solid #000; 
    border-radius: 10px;
    margin-left: 10px;
    font-weight: 700;
    cursor: pointer;
`;
const OrderTypeBtn = styled.button`
    background: none;
	color: inherit;
	border: none;
	padding: 0;
	font: inherit;
	cursor: pointer;
	outline: inherit;
`;

const PlayerSearch = (props) => {
    const [teams, setTeams] = useState([]);
    
    useEffect(() => {
        fetch("/all_teams")
            .then((response) => response.json())
            .then((data) => setTeams(data));
    },[])

    const { register, handleSubmit } = useForm();  
    const [items, setItems] = useState([]);
    const [itemNum, setItemNum] = useState([]);
    // console.log(itemNum[0]["COUNT(id)"])
    const [orderType, setOrderType] = useState('points_per_game');
    const [isDesc, setIsDesc] = useState(true);
    const DescOrAsc = () => {
        if (isDesc) {
            return ('DESC')
        } else {
            return ('ASC')
        }
    }

    const onSubmit = (data, e) => {
        // console.log(isDesc)
        fetch(`/select_team/${currentPage}/${orderType}/${DescOrAsc()}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        }).then((response) => response.json())
        .then((data) => setItems(data));

        fetch("/check_data_num", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        }).then((response) => response.json())
        .then((data) => setItemNum(data));
    }
    const onError = (errors, e) => console.log(errors, e);

    const [pageList, setPageList] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    useEffect(() => {
        const perpage = 15;
        const pageTotal = Math.ceil(itemNum[0] && itemNum[0]["COUNT(id)"] / perpage);
        let lists = [];
        for (let i=0; i<=pageTotal-1; i++) {
            lists.push(`${i+1}`);
            setPageList(lists);
        }
    },[itemNum])

    // window.onload = function() {
    //     document.forms['nba'].submit();
    // }
    const formRef = useRef(null);
    // const inputRef = document.getElementById('input');
    

    return (
        <Container>
            <form onSubmit={handleSubmit(onSubmit, onError)} ref={formRef}>
                <SearchWrapper>
                    <SelectorWrapper>
                        Teams:
                        <select {...register("team")}>
                            <option>All</option>
                            {teams.map((team, index) => {
                                return (
                                    <option key={index}>{team.team_name}</option>
                                )
                            })}
                        </select>
                    </SelectorWrapper>
                    <SubmitWrapper>
                        {/* <SubmitBtn type="submit" value="search" /> */}
                        <SubmitBtn onClick={formRef.current && formRef.current.submit}>search</SubmitBtn>
                    </SubmitWrapper>
                </SearchWrapper>
                <TableWrapper>
                    <table>
                        <colgroup></colgroup>
                        <thead>
                            <tr>
                                <th>
                                    TeamName
                                </th>
                                <th>
                                    Name
                                </th>
                                <th>
                                    <OrderTypeBtn onClick={() => setOrderType('games_played')} type="submit">
                                        Games
                                    </OrderTypeBtn>
                                    <OrderTypeBtn onClick={() => setIsDesc(!isDesc)} type="submit">
                                        <span className="material-icons">unfold_more</span>
                                    </OrderTypeBtn>
                                </th>
                                <th>
                                    <OrderTypeBtn onClick={() => setOrderType('points_per_game')} type="submit">
                                        Points
                                    </OrderTypeBtn>
                                    <OrderTypeBtn onClick={() => setIsDesc(!isDesc)} type="submit">
                                        <span className="material-icons">unfold_more</span>
                                    </OrderTypeBtn>
                                </th>
                                <th>
                                    <OrderTypeBtn onClick={() => setOrderType('rebounds_per_game')} type="submit">
                                        Rebounds
                                    </OrderTypeBtn>
                                </th>
                                <th>
                                    <OrderTypeBtn onClick={() => setOrderType('assists_per_game')} type="submit">
                                        Assists
                                    </OrderTypeBtn>
                                </th>
                                <th>
                                    <OrderTypeBtn onClick={() => setOrderType('steals_per_game')} type="submit">
                                        Steals
                                    </OrderTypeBtn>
                                </th>
                                <th>
                                    <OrderTypeBtn onClick={() => setOrderType('blocks_per_game')} type="submit">
                                        Blocks
                                    </OrderTypeBtn>
                                </th>
                                <th>Detail</th>
                            </tr>
                        </thead>
                        <tbody>
                            {items.map((item, index) => {
                                return (
                                    <tr key={index}>
                                        {Object.getOwnPropertyNames(item).map((attr, index) => {
                                            if(index===0) return null;
                                            return (
                                                <td key={attr}>
                                                    {item[attr]}
                                                </td>
                                            )
                                        })}
                                        <td>
                                            <Link to={`players/${item.id}`}>
                                                <span className="material-icons">search</span>
                                            </Link>
                                        </td>
                                    </tr>
                                )
                            })}
                        </tbody>
                    </table>
                </TableWrapper>
                {/* {itemNum[0]["COUNT(id)"] > 15 && itemNum[0]["COUNT(id)"]} */}
                <PaginationWrapper>
                    {pageList && pageList.map((page, index) => {
                        // console.log(Number(page), currentPage)
                        if (Math.abs((index+1)-currentPage)>2 && index!==pageList.length-1 && index!==pageList[0]-1) {
                            return (<>.</>)
                        }
                        return (
                            <PaginationBtn key={index} onClick={() => setCurrentPage(Number(page))} active={Number(page)===currentPage} type="submit">
                                {page}
                            </PaginationBtn>
                        )
                    })}
                </PaginationWrapper>
            </form>
        </Container>
    )
}

export default PlayerSearch;
