import React, {useEffect, useState} from 'react';
import styled from "styled-components";
import { useForm } from "react-hook-form";
import { BrowserRouter, Route, Routes } from "react-router-dom";

//Styled
const Container = styled.div`
    position: relative;
    top: 150px;
    width: 80%;
`;
const SearchWrapper = styled.div`
    position: relative;
    min-height: 100px;
    left: 10%;
    padding: 20px;
    background-color: transparent;
    border: 2px solid #000;
`;
const SelectorWrapper = styled.div`
    display: flex;
    align-items: center;
    font-size: 18px;
    font-weight: 600;

    > select {
        font-size: 18px;
        margin-left: 10px;
    }
`;
const SubmitWrapper = styled.div`
    position: absolute;
    bottom: 20px;
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
`;
const SubmitBtn = styled.input`
    background-color: transparent;
    border: 2px solid #000;
    font-size: 24px;  
`;
const TableWrapper = styled.div`
    position: relative;
    top: 20px;
    left: 10%;
    border: 2px solid #333;
    > table {
        width: 100%;
        border-collapse: collapse;
    }
    colgroup {
        border-right: 1px solid #000;
        
    }
    thead > tr {
        background-color: #EEEEEE;
    }
    tbody > tr {
        &:nth-child(2n) {
            background-color: #EEEEEE;
        }
    }
    td, th {
        border-left: 2px solid #000;
        &:first-child {
            border-left: none;
        }
        text-align: center;
        padding: 5px;

        > span {
            font-size: 24px;
            cursor: pointer;
        }
    }
`;
const PaginationWrapper = styled.div`
    position: relative;
    top: 30px;
    left: 10%;
    display: flex;
    justify-content: flex-end;  
`;
const PaginationBtn = styled.button`
    width: 30px;
    height: 30px;
    display: flex;
    align-items: center;
    justify-content: center; 
    background-color: ${props => props.active ? '#8FB9F7' : 'trasparent'};
    border: 2px solid #000; 
    border-radius: 10px;
    margin-left: 10px;
    font-weight: 700;
    cursor: pointer;
`;

const PlayerSearchContent = () => {
    
}

const PlayerSearch = (props) => {
    const [teams, setTeams] = useState([]);
    
    useEffect(() => {
        fetch("/all_teams")
            .then((response) => response.json())
            .then((data) => setTeams(data));
    },[])

    const { register, handleSubmit } = useForm();  
    const [items, setItems] = useState([]);
    const [itemNum, setItemNum] = useState([]);
    // console.log(itemNum[0]["COUNT(id)"])

    const onSubmit = (data, e) => {
        // console.log(currentPage)
        fetch(`/select_team/${currentPage}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        }).then((response) => response.json())
        .then((data) => setItems(data));

        fetch("/check_data_num", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        }).then((response) => response.json())
        .then((data) => setItemNum(data));
    }
    const onError = (errors, e) => console.log(errors, e);

    const [pageList, setPageList] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    useEffect(() => {
        const perpage = 15;
        const pageTotal = Math.ceil(itemNum[0] && itemNum[0]["COUNT(id)"] / perpage);
        let lists = [];
        for (let i=0; i<=pageTotal-1; i++) {
            lists.push(`${i+1}`);
            setPageList(lists);
        }
    },[itemNum])

    return (
        <Routes>
            <Route></Route>
        </Routes>
        
    )
}

export default PlayerSearch;
