import React, {useEffect, useState} from 'react';
import styled from "styled-components";
import { useForm } from "react-hook-form";

//Styled
const Container = styled.div`
    position: relative;
    top: 150px;
    width: 80%;
`;
const SearchWrapper = styled.div`
    position: relative;
    min-height: 100px;
    left: 10%;
    padding: 20px;
    background-color: transparent;
    border: 2px solid #000;
`;
const SelectorWrapper = styled.div`
    display: flex;
    align-items: center;
    font-size: 18px;
    font-weight: 600;

    > select {
        font-size: 18px;
        margin-left: 10px;
    }
`;
const SubmitWrapper = styled.div`
    position: absolute;
    bottom: 20px;
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
`;
const SubmitBtn = styled.input`
    background-color: transparent;
    border: 2px solid #000;
    font-size: 24px;  
`;
const TableWrapper = styled.div`
    position: relative;
    top: 20px;
    left: 10%;
    border: 2px solid #333;
    > table {
        width: 100%;
        border-collapse: collapse;
    }
    colgroup {
        border-right: 1px solid #000;
        
    }
    thead > tr {
        background-color: #EEEEEE;
    }
    tbody > tr {
        &:nth-child(2n) {
            background-color: #EEEEEE;
        }
    }
    td, th {
        border-left: 2px solid #000;
        &:first-child {
            border-left: none;
        }
        text-align: center;
        padding: 5px;

        > span {
            font-size: 24px;
            cursor: pointer;
        }
    }
`;
const PaginationWrapper = styled.div`
    display: flex;
    justify-content: space-around;  
`;
const PaginationBtn = styled.div`
    width: 30px;
    height: 30px;
    display: flex;
    align-items: center;
    justify-content: center;  
`;

const PlayerSearch = (props) => {
    const [teams, setTeams] = useState([]);
    
    useEffect(() => {
        fetch("/all_teams")
            .then((response) => response.json())
            .then((data) => setTeams(data));
    },[])

    const { register, handleSubmit } = useForm();  
    const [items, setItems] = useState([]);
    const [itemNum, setItemNum] = useState([]);
    // console.log(itemNum[0]["COUNT(id)"])

    const onSubmit = (data, e) => {
        fetch("/select_team", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        }).then((response) => response.json())
        .then((data) => setItems(data));

        fetch("/check_data_num", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        }).then((response) => response.json())
        .then((data) => setItemNum(data));
    }
    const onError = (errors, e) => console.log(errors, e);

    const [pageLists, setPageLists] = useState([]);
    useEffect(() => {
        const perpage = 15;
        const pageTotal = Math.ceil(itemNum[0] && itemNum[0]["COUNT(id)"] / perpage);
        let lists = [];
        for (let i=0; i<=pageTotal-1; i++) {
            lists.push(`${i+1}`);
            setPageLists(lists);
        }
    },[itemNum])

    return (
        <Container>
            <SearchWrapper>
                <form onSubmit={handleSubmit(onSubmit, onError)}>
                    <SelectorWrapper>
                        Teams:
                        <select {...register("team")}>
                            <option>All</option>
                            {teams.map((team, index) => {
                                return (
                                    <option key={index}>{team.team_name}</option>
                                )
                            })}
                        </select>
                    </SelectorWrapper>
                    <SubmitWrapper>
                        <SubmitBtn type="submit" value="search" />
                    </SubmitWrapper>
                </form>
            </SearchWrapper>
            <TableWrapper>
                <table>
                    <colgroup></colgroup>
                    <thead>
                        <tr>
                            <th>TeamName</th>
                            <th>Name</th>
                            <th>Games</th>
                            <th>Points</th>
                            <th>Rebounds</th>
                            <th>Assists</th>
                            <th>Steals</th>
                            <th>Blocks</th>
                            <th>Detail</th>
                        </tr>
                    </thead>
                    <tbody>
                        {items.map((item, index) => {
                            // console.log(Object.getOwnPropertyNames(item))
                            return (
                                <tr key={index}>
                                    {Object.getOwnPropertyNames(item).map((attr, index) => {
                                        return (
                                            <td key={attr}>
                                                {item[attr]}
                                            </td>
                                        )
                                    })}
                                    <td>
                                        <span className="material-icons">search</span>
                                    </td>
                                </tr>
                            )
                        })}
                    </tbody>
                </table>
            </TableWrapper>
            {/* {itemNum[0]["COUNT(id)"] > 15 && itemNum[0]["COUNT(id)"]} */}
            <PaginationWrapper>
                {pageLists && pageLists.map((pageList, index) => {
                    console.log(pageList)
                    return pageList;
                })}
            </PaginationWrapper>
        </Container>
    )
}

export default PlayerSearch;
